from base64 import b64decode
import os

class DeviceConfig:
	def __init__(self):
		self.session_id_type = 'android'
		self.send_key_control_nonce = True

		self.private_key_available = True
		self.device_private_key = b64decode('')
		self.device_client_id_blob = b64decode('')

		self.vmp = False
		self.device_vmp_blob = None

	def load_custom_device(self, path):
		file_client_id = file_name = os.path.join(path, 'client_id.bin')
		file_private_key = os.path.join(path, 'private_key.pem')

		if os.path.isfile(file_client_id) and os.path.isfile(file_private_key):
			with open(file_client_id, 'rb') as f:
				self.device_client_id_blob = f.read()

			with open(file_private_key, 'rb') as f:
				self.device_private_key = f.read()

			return True

		return False
